import React from 'react';

import success from '../images/success.svg';
import fail from '../images/fail.svg';

function InfoTooltip({ isLoggedIn, isOpen }) {
    const textSuccess = 'Вы успешно зарегистрировались!';
    const textFail = 'Что-то пошло не так! Попробуйте ещё раз.';

    return (
        <div className={`popup popup-info ${isOpen && 'popup_opened'}`}>
            <button
                className="button-close popup__close popup__button-close"
                type="button" 
                />

            <div className="popup__info-container">
                <img className="popup-info__img" src={isLoggedIn ? success : fail} alt="" />
                <h2 className="popup-info__title">{textSuccess}</h2>
            </div>
        </div>
    )
}

export default InfoTooltip;
