import React from 'react';

function InfoTooltip({ isOpen, on }) {

    return (
        <div className={`popup popup-info ${isOpen && 'popup_opened'}`}>
            <button
                className="button-close popup__cross popup__cross_btn_close-profile "
                type="button" 
                />

            <div className="popup__info-container">
                <img className="popup-info__img" src={isLoggedIn ? success : fail} alt="" />
                <h2 className="popup-info__title">{isLoggedIn ? textSuccess : textFail}</h2>
            </div>
        </div>
    )
}

export default InfoTooltip;
